ref.Programmability.CreateCommand:
create FUNCTION [doc].[GetActivityOfSBPbyPlanActivity_Activity_A](@tpid [int]) RETURNS [int] AS BEGIN declare @result int;
declare @sbp int; declare @sbp_p int; declare @act int; declare @cont int; declare @doc_a int; declare @doc_p int; declare @maxdate datetime;
select @sbp=d.IdSBP, @act=tp.idActivity, @cont=tp.idContingent, @doc_p=d.id from doc.PlanActivity d join tp.PlanActivity_Activity tp on tp.idOwner=d.id where tp.id=@tpid;
select @maxdate=max([date]) from reg.ExecutedOperation eo where eo.idEntityOperation=-2013265866 and eo.idRegistratorEntity=-2013265436 and eo.idRegistrator=@doc_p;
--select eo.id, o.Name, e.id from ref.EntityOperation eo --join ref.Entity as e on e.Name='PlanActivity' and e.id=eo.[idEntity] --join ref.Operation as o on eo.idOperation=o.id and o.name='Process'
with sbpTree as ( select S1.id, S1.idParent, S1.idSBPType from ref.SBP S1 where S1.id = @sbp union all select S2.id, S2.idParent, S2.idSBPType from ref.SBP S2 inner join sbpTree T on S2.id = T.idParent ) select @sbp_p = T.id from sbpTree T where T.idSBPType IN (1,2)
select top 1 @result=idRegistrator from ( select distinct ap.idRegistrator from reg.TaskCollection as tc join reg.TaskVolume tv on tv.idTaskCollection=tc.id and tv.idSBP=@sbp_p --and tv.idTerminator is null join reg.AttributeOfProgram as ap on ap.idProgram = tv.idProgram and ap.idTerminator is null where tc.idActivity=@act and ISNULL(tc.idContingent,0)=ISNULL(@cont,0) and tv.DateCreate <= @maxdate and (tv.DateTerminate is null or tv.DateTerminate > @maxdate) union select distinct ap.idRegistrator from reg.TaskCollection as tc join reg.TaskVolume tv on tv.idTaskCollection=tc.id and tv.idSBP=@sbp_p --and tv.idTerminator is null join reg.AttributeOfProgram as ap on ap.idProgram = tv.idProgram and ap.idTerminator is null where tc.idActivity=@act and ISNULL(tc.idContingent,0)=ISNULL(@cont,0) ) as m
RETURN @result END
